import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [user, setUser] = useState(null);

  // Učitaj korisnika iz localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    // opcionalno: redirect na početnu
    window.location.href = "/";
  };

  const isAdmin = user?.role === "admin";
  const isLoggedIn = !!user;

  return (
    <nav className="flex justify-between items-center p-4 shadow-md bg-white">
      {/* Lijeva strana: logo i glavni linkovi */}
      <div className="flex items-center space-x-6">
        <Link to="/" className="text-xl font-bold">
          Atelier Noir
        </Link>
        <Link to="/" className="hover:text-gray-700">Početna</Link>
        <Link to="/o-nama" className="hover:text-gray-700">O nama</Link>
        <Link to="/ponuda" className="hover:text-gray-700">Ponuda</Link>
        <Link to="/kontakt" className="hover:text-gray-700">Kontakt</Link>
      </div>

      {/* Desna strana: zavisno od korisnika */}
      <div className="flex items-center space-x-4">
        {!isLoggedIn ? (
          <>
            <Link to="/prijava" className="hover:text-gray-700">Prijava</Link>
            <Link to="/registracija" className="hover:text-gray-700">Registracija</Link>
          </>
        ) : isAdmin ? (
          <>
            <Link to="/admin" className="hover:text-gray-700">Admin</Link>
            <button onClick={handleLogout} className="hover:text-gray-700">Odjavi se</button>
          </>
        ) : (
          <>
            <Link to="/korpa" className="hover:text-gray-700">Korpa</Link>
            <button onClick={handleLogout} className="hover:text-gray-700">Odjavi se</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
