// src/components/Navbar.jsx
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/"); // ili refresh: window.location.reload();
  };

  return (
    <nav className="navbar">
      <ul className="nav-links">
        {user ? (
          user.role === "admin" ? (
            <li><Link to="/admin">Admin</Link></li>
          ) : (
            <li><Link to="/korpa">Korpa</Link></li>
          )
        ) : (
          <>
            <li><Link to="/products">Kafa</Link></li>
            <li><Link to="/about">O nama</Link></li>
          </>
        )}
      </ul>

      <div className="auth-buttons">
        {user ? (
          <button className="btn" onClick={handleLogout}>Odjavi se</button>
        ) : (
          <>
            <Link to="/login" className="btn">Prijavi se</Link>
            <Link to="/signup" className="btn primary">Registruj se</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
