import React, { useState, useEffect } from "react";

const AdminPanel = () => {
  const [kategorija, setKategorija] = useState("svi");
  const [naziv, setNaziv] = useState("");
  const [opis, setOpis] = useState("");
  const [gramaza, setGramaza] = useState("");
  const [cijena, setCijena] = useState("");
  const [slika, setSlika] = useState("");
  const [proizvodi, setProizvodi] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/api/products/${kategorija}`)
      .then((res) => res.json())
      .then((data) => setProizvodi(data))
      .catch((err) => console.error(err));
  }, [kategorija]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!naziv || !opis || !gramaza || !cijena || !slika) {
      alert("Molimo popunite sva polja.");
      return;
    }

    const noviProizvod = { naziv, opis, gramaza, cijena, slika };

    try {
      const response = await fetch(
        `http://localhost:5000/api/dodaj/${kategorija}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(noviProizvod),
        }
      );

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setNaziv("");
        setOpis("");
        setGramaza("");
        setCijena("");
        setSlika("");
        setProizvodi([...proizvodi, noviProizvod]);
      } else {
        alert("Greška: " + (data.error || "Nešto nije u redu."));
      }
    } catch (error) {
      alert("Greška pri slanju podataka na server.");
      console.error(error);
    }
  };

  const obrisiProizvod = async (nazivProizvoda) => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/obrisi/${kategorija}/${nazivProizvoda}`,
        {
          method: "DELETE",
        }
      );
      const data = await res.json();
      alert(data.message);
      setProizvodi(proizvodi.filter((p) => p.naziv !== nazivProizvoda));
    } catch (err) {
      console.error(err);
      alert("Greška pri brisanju proizvoda.");
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "auto", padding: 20 }}>
      <h2>Admin Panel</h2>

      <label>Kategorija:</label>
      <select
        value={kategorija}
        onChange={(e) => setKategorija(e.target.value)}
      >
        <option value="svi">Svi</option>
        <option value="preporuke">Preporuke</option>
        <option value="best">Best</option>
      </select>

      <form onSubmit={handleSubmit} style={{ marginTop: 20 }}>
        <div>
          <label>Naziv:</label>
          <input
            type="text"
            value={naziv}
            onChange={(e) => setNaziv(e.target.value)}
          />
        </div>
        <div>
          <label>Opis:</label>
          <textarea
            value={opis}
            onChange={(e) => setOpis(e.target.value)}
          />
        </div>
        <div>
          <label>Gramaža:</label>
          <input
            type="text"
            value={gramaza}
            onChange={(e) => setGramaza(e.target.value)}
          />
        </div>
        <div>
          <label>Cijena:</label>
          <input
            type="text"
            value={cijena}
            onChange={(e) => setCijena(e.target.value)}
          />
        </div>
        <div>
          <label>Putanja do slike:</label>
          <input
            type="text"
            value={slika}
            onChange={(e) => setSlika(e.target.value)}
          />
        </div>
        <button type="submit" style={{ marginTop: 10 }}>
          Dodaj proizvod
        </button>
      </form>

      <h3 style={{ marginTop: 30 }}>Proizvodi u kategoriji "{kategorija}"</h3>
      <ul>
        {proizvodi.map((p) => (
          <li key={p.naziv}>
            {p.naziv} - {p.cijena}
            <button
              onClick={() => obrisiProizvod(p.naziv)}
              style={{ marginLeft: 10 }}
            >
              Obriši
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPanel;
