import React, { useState, useEffect } from "react";
import data from "../data/data.json"; // prilagodi putanju po potrebi
import "../css/style.css"; // ako imaš posebne stilove

const AdminPanel = () => {
  const [proizvodi, setProizvodi] = useState([]);

  useEffect(() => {
    // Load proizvodi iz json-a
    setProizvodi(data.svi);
  }, []);

  const obrisiProizvod = (index) => {
    const noviProizvodi = [...proizvodi];
    noviProizvodi.splice(index, 1);
    setProizvodi(noviProizvodi);
    // Ovde bi išao API poziv da se ažurira backend (ako postoji)
  };

  return (
    <div className="admin-panel">
      <h2>Admin Panel - Upravljanje Proizvodima</h2>
      <table className="admin-table">
        <thead>
          <tr>
            <th>Slika</th>
            <th>Naziv</th>
            <th>Opis</th>
            <th>Cijena</th>
            <th>Gram.</th>
            <th>Akcija</th>
          </tr>
        </thead>
        <tbody>
          {proizvodi.map((proizvod, index) => (
            <tr key={index}>
              <td>
                <img src={proizvod.slika} alt={proizvod.naziv} width="60" />
              </td>
              <td>{proizvod.naziv}</td>
              <td>{proizvod.opis}</td>
              <td>{proizvod.cijena}</td>
              <td>{proizvod.gramaza}</td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => obrisiProizvod(index)}
                >
                  Obriši
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
