import React, { useEffect, useState } from "react";

const Korpa = () => {
  const [proizvodi, setProizvodi] = useState([]);
  const [loading, setLoading] = useState(true);
  const email = localStorage.getItem("userEmail");

  useEffect(() => {
    if (!email) {
      setLoading(false);
      return;
    }

    fetch(`http://localhost:5000/api/cart/${email}`)
      .then((res) => res.json())
      .then((data) => {
        setProizvodi(data || []);
        setLoading(false);
      })
      .catch(() => {
        setProizvodi([]);
        setLoading(false);
      });
  }, [email]);

  if (!email) {
    return <p>Morate biti prijavljeni da biste vidjeli korpu.</p>;
  }

  if (loading) {
    return <p>Uƒçitavanje korpe...</p>;
  }

  return (
    <div>
      <h2>Korpa</h2>
      <ul>
        {proizvodi.length === 0 ? (
          <li>Korpa je prazna</li>
        ) : (
          proizvodi.map((p, i) => (
            <li key={i}>
              {p.naziv} x {p.kolicina}
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default Korpa;
