import React, { useState } from "react";

const AdminPanel = () => {
  const [naziv, setNaziv] = useState("");
  const [opis, setOpis] = useState("");
  const [gramaza, setGramaza] = useState("");
  const [cijena, setCijena] = useState("");
  const [slika, setSlika] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!naziv || !opis || !gramaza || !cijena || !slika) {
      alert("Molimo popunite sva polja.");
      return;
    }

    const noviProizvod = {
      naziv,
      opis,
      gramaza,
      cijena,
      slika,
    };

    try {
      const response = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(noviProizvod),
      });

      if (response.ok) {
        alert("Proizvod je uspješno dodat u kategoriju 'svi'.");
        // Reset forme
        setNaziv("");
        setOpis("");
        setGramaza("");
        setCijena("");
        setSlika("");
      } else {
        const data = await response.json();
        alert("Greška: " + (data.error || "Nešto nije u redu."));
      }
    } catch (error) {
      alert("Greška pri slanju podataka na server.");
      console.error(error);
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: "auto", padding: 20 }}>
      <h2>Admin panel - Dodaj proizvod u 'svi'</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Naziv:</label><br />
          <input
            type="text"
            value={naziv}
            onChange={(e) => setNaziv(e.target.value)}
          />
        </div>
        <div>
          <label>Opis:</label><br />
          <textarea
            value={opis}
            onChange={(e) => setOpis(e.target.value)}
          />
        </div>
        <div>
          <label>Gramaža:</label><br />
          <input
            type="text"
            value={gramaza}
            onChange={(e) => setGramaza(e.target.value)}
            placeholder="npr. 250g"
          />
        </div>
        <div>
          <label>Cijena:</label><br />
          <input
            type="text"
            value={cijena}
            onChange={(e) => setCijena(e.target.value)}
            placeholder="npr. $12.50"
          />
        </div>
        <div>
          <label>Putanja do slike:</label><br />
          <input
            type="text"
            value={slika}
            onChange={(e) => setSlika(e.target.value)}
            placeholder="npr. images/novi_proizvod.png"
          />
        </div>
        <br />
        <button type="submit">Dodaj proizvod</button>
      </form>
    </div>
  );
};

export default AdminPanel;
