import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Navbar.css"; // Obavezno napravi i poveži CSS ako već nije

const Navbar = () => {
  const [user, setUser] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    const handleStorageChange = () => {
      const updatedUser = JSON.parse(localStorage.getItem("user"));
      setUser(updatedUser);
    };

    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/");
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="logo">
          <Link to="/">Atelier Noir</Link>
        </div>

        <div className="hamburger" onClick={() => setIsOpen(!isOpen)}>
          ☰
        </div>

        <ul className={`nav-links ${isOpen ? "active" : ""}`}>
          <li><Link to="/" onClick={() => setIsOpen(false)}>Početna</Link></li>
          <li><Link to="/o_nama" onClick={() => setIsOpen(false)}>O nama</Link></li>
          <li><Link to="/ponuda" onClick={() => setIsOpen(false)}>Ponuda</Link></li>
          <li><Link to="/kontakt" onClick={() => setIsOpen(false)}>Kontakt</Link></li>
          {user && user.role === "admin" && (
            <li><Link to="/admin" onClick={() => setIsOpen(false)}>Admin</Link></li>
          )}
          {user && user.role !== "admin" && (
            <li><Link to="/korpa" onClick={() => setIsOpen(false)}>Korpa</Link></li>
          )}
        </ul>

        <div className="auth-buttons">
          {user ? (
            <button className="btn primary" onClick={handleLogout}>Odjavi se</button>
          ) : (
            <>
              <Link to="/log-in" className="btn">Prijavi se</Link>
              <Link to="/sign-up" className="btn primary">Registruj se</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
