import React, { useState, useEffect, useRef } from "react";

function Counter({ target, duration = 1000 }) {
  const [count, setCount] = useState(0);
  const ref = useRef();
  const started = useRef(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !started.current) {
          started.current = true;
          let start = 0;
          const end = parseFloat(target);
          // Osiguraj minimalno trajanje intervala, npr. 10ms, i maksimalno 100ms
          const stepTime = Math.min(Math.max(Math.floor(duration / end), 10), 100);

          const timer = setInterval(() => {
            start += 1;
            setCount((prev) => {
              if (start >= end) {
                clearInterval(timer);
                return end;
              }
              return start;
            });
          }, stepTime);
        }
      },
      { threshold: 0.5 }
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    };
  }, [target, duration]);

  return (
    <strong ref={ref} className="counter">
      {Number.isInteger(target) ? Math.floor(count) : count.toFixed(1)}
    </strong>
  );
}

export default Counter;
