import React, { useEffect } from "react";
import { useLocation, Link } from "react-router-dom";

function Navbar({ isLoggedIn, setIsLoggedIn, isAdmin, setIsAdmin }) {
  const location = useLocation();

  useEffect(() => {
    // Primjer: ako je ruta /admin, postavi isAdmin na true, inače false
    if (location.pathname.startsWith("/admin")) {
      setIsAdmin(true);
    } else {
      setIsAdmin(false);
    }

    // Primjer: ako postoji token u localStorage, korisnik je logovan
    const korisnik = JSON.parse(localStorage.getItem("korisnik"));
    if (korisnik && korisnik.token) {
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
    }
  }, [location, setIsAdmin, setIsLoggedIn]);

  function logout() {
    localStorage.removeItem("korisnik");
    setIsLoggedIn(false);
    setIsAdmin(false);
  }

  return (
    <nav>
      <ul>
        <li>
          <Link to="/">Početna</Link>
        </li>

        {!isLoggedIn && (
          <>
            <li>
              <Link to="/log-in">Prijava</Link>
            </li>
            <li>
              <Link to="/register">Registracija</Link>
            </li>
          </>
        )}

        {isLoggedIn && (
          <>
            <li>
              <button onClick={logout}>Odjava</button>
            </li>
            {isAdmin && (
              <li>
                <Link to="/admin">Admin Panel</Link>
              </li>
            )}
          </>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
