import React, { useEffect, useState } from "react";

function AdminPanel() {
  const [korisnik, setKorisnik] = useState(null);
  const [preporuke, setPreporuke] = useState([]);
  const [best, setBest] = useState([]);
  const [svi, setSvi] = useState([]);
  const [loading, setLoading] = useState(true);

  // Form state
  const [naziv, setNaziv] = useState("");
  const [opis, setOpis] = useState("");
  const [gramaza, setGramaza] = useState("");
  const [cijena, setCijena] = useState("");
  const [slika, setSlika] = useState(null);

  useEffect(() => {
    const korisnikLocal = JSON.parse(localStorage.getItem("korisnik"));
    if (!korisnikLocal || korisnikLocal.role !== "admin") {
      window.location.href = "log-in.html";
      return;
    }
    setKorisnik(korisnikLocal);
    fetchKafe();
  }, []);

  async function fetchKafe() {
    setLoading(true);
    try {
      const res = await fetch("/dohvatiKafu");
      const data = await res.json();
      setPreporuke(data.preporuke);
      setBest(data.best);
      setSvi(data.svi);
    } catch (err) {
      alert("Greška pri dohvatanju kafa.");
    }
    setLoading(false);
  }

  async function dodajKafu(e) {
    e.preventDefault();
    if (!slika) {
      alert("Molimo izaberite sliku.");
      return;
    }
    const formData = new FormData();
    formData.append("naziv", naziv.trim());
    formData.append("opis", opis.trim());
    formData.append("gramaza", gramaza.trim());
    formData.append("cijena", cijena.trim());
    formData.append("slika", slika);

    try {
      const res = await fetch("/upisi", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      if (data.success) {
        alert("Kafa uspešno dodata!");
        setNaziv("");
        setOpis("");
        setGramaza("");
        setCijena("");
        setSlika(null);
        fetchKafe();
      } else {
        alert("Greška: " + data.message);
      }
    } catch {
      alert("Greška prilikom dodavanja.");
    }
  }

  async function dodajUKategoriju(kategorija, naziv) {
    try {
      const res = await fetch("/dodajUKategoriju", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ kategorija, naziv }),
      });
      const data = await res.json();
      alert(data.message);
      if (data.success) fetchKafe();
    } catch {
      alert("Greška pri dodavanju u kategoriju.");
    }
  }

  async function ukloniIzKategorije(kategorija, naziv) {
    if (!window.confirm(`Da li ste sigurni da želite da uklonite "${naziv}" iz ${kategorija}?`))
      return;
    try {
      const res = await fetch("/ukloniIzKategorije", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ kategorija, naziv }),
      });
      const data = await res.json();
      alert(data.message);
      if (data.success) fetchKafe();
    } catch {
      alert("Greška pri uklanjanju iz kategorije.");
    }
  }

  // Render funkcije za kategorije i sve kafe
function renderKategoriju(kafe, kategorija) {
  if (kafe.length === 0) return <p>Nema kafa u kategoriji.</p>;
  return kafe.map((kafa) => (
    <div className="kafa" key={`${kategorija}-${kafa.naziv}`}>
      <strong>{kafa.naziv}</strong> — {kafa.cijena} KM / {kafa.gramaza}
      <button onClick={() => ukloniIzKategorije(kategorija, kafa.naziv)}>Ukloni</button>
    </div>
  ));
}


function renderSve() {
  if (svi.length === 0) return <p>Nema kafa.</p>;

  const preporukeNazivi = new Set(preporuke.map((k) => k.naziv));
  const bestNazivi = new Set(best.map((k) => k.naziv));

  return svi.map((kafa) => (
    <div className="kafa" key={"svi-" + kafa.naziv}>
      <span>{kafa.naziv} - {kafa.cijena} KM</span>

      {!preporukeNazivi.has(kafa.naziv) ? (
        <button
          onClick={() => dodajUKategoriju("preporuke", kafa.naziv)}
          disabled={preporuke.length >= 4}
        >
          Dodaj u preporuke
        </button>
      ) : (
        <span style={{ marginLeft: "8px" }}>(već u preporukama)</span>
      )}

      {!bestNazivi.has(kafa.naziv) ? (
        <button
          onClick={() => dodajUKategoriju("best", kafa.naziv)}
          disabled={best.length >= 4}
        >
          Dodaj u best
        </button>
      ) : (
        <span style={{ marginLeft: "8px" }}>(već u best)</span>
      )}
    </div>
  ));
}


  return (
    <div className="container">
      <div className="left">
        <h1>Dobrodošao, admin!</h1>

        <section className="form-wrapper">
          <h2>Dodaj novu kafu (dodaje se u "svi")</h2>
          <form id="dodajForm" onSubmit={dodajKafu}>
            <input
              type="text"
              placeholder="Naziv"
              value={naziv}
              onChange={(e) => setNaziv(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Opis"
              value={opis}
              onChange={(e) => setOpis(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Gramaža"
              value={gramaza}
              onChange={(e) => setGramaza(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Cena"
              value={cijena}
              onChange={(e) => setCijena(e.target.value)}
              required
            />
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setSlika(e.target.files[0])}
              required
            />
            <button type="submit">Dodaj</button>
          </form>
        </section>

        <section className="section">
          <h2>Preporučene kafe (max 4)</h2>
          <div id="preporukeList" className="kafa-list">
            {loading ? "Učitavanje..." : renderKategoriju(preporuke, "preporuke")}
          </div>
        </section>

        <section className="section">
          <h2>Najbolje kafe (max 4)</h2>
          <div id="bestList" className="kafa-list">
            {loading ? "Učitavanje..." : renderKategoriju(best, "best")}
          </div>
        </section>

        <section className="section">
          <h2>Sve kafe</h2>
          <div id="sviList" className="kafa-list">{loading ? "Učitavanje..." : renderSve()}</div>
        </section>
      </div>

      <div className="right"></div>
    </div>
  );
}

export default AdminPanel;
