import React from "react";

const Korpa = ({ proizvodi }) => {
  if (!proizvodi) {
    return <p>Korpa je prazna</p>;
  }

useEffect(() => {
  const email = localStorage.getItem("userEmail");

  fetch("http://localhost:5000/korpa?email=" + email)
    .then((res) => res.json())
    .then((data) => setKorpa(data.korpa || []));
}, []);


  return (
    <div>
      <h2>Korpa</h2>
      <ul>
        {proizvodi.length === 0 ? (
          <li>Korpa je prazna</li>
        ) : (
          proizvodi.map((p, i) => <li key={i}>{p.naziv} x {p.kolicina}</li>)
        )}
      </ul>
    </div>
  );
};
export default Korpa;
