import React, { useEffect, useState } from "react";
import "../css/admin.css";

const kategorije = ["svi", "preporuke", "best"];

const AdminPanel = () => {
  const [data, setData] = useState({});
  const [loading, setLoading] = useState(true);

  const fetchData = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/products");
      const json = await res.json();
      setData(json);
      setLoading(false);
    } catch (error) {
      console.error("Greška pri dohvaćanju podataka:", error);
    }
  };

  const obrisiProizvod = async (kategorija, naziv) => {
    try {
      const res = await fetch(`http://localhost:5000/api/obrisi/${kategorija}/${naziv}`, {
        method: "DELETE",
      });
      const result = await res.json();
      alert(result.message);
      fetchData(); // osvježi prikaz
    } catch (err) {
      console.error("Greška pri brisanju:", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  if (loading) return <p>Učitavanje...</p>;

  return (
    <div className="admin-panel">
      <h2>Admin Panel - Pregled i Brisanje Proizvoda</h2>

      {kategorije.map((kategorija) => (
        <div key={kategorija}>
          <h3>Kategorija: {kategorija}</h3>
          <table className="admin-table">
            <thead>
              <tr>
                <th>Naziv</th>
                <th>Opis</th>
                <th>Gramaža</th>
                <th>Cijena</th>
                <th>Akcija</th>
              </tr>
            </thead>
            <tbody>
              {data[kategorija] && data[kategorija].length > 0 ? (
                data[kategorija].map((proizvod, index) => (
                  <tr key={index}>
                    <td>{proizvod.naziv}</td>
                    <td>{proizvod.opis}</td>
                    <td>{proizvod.gramaza}</td>
                    <td>{proizvod.cijena}</td>
                    <td>
                      <button
                        className="btn-danger"
                        onClick={() => obrisiProizvod(kategorija, proizvod.naziv)}
                      >
                        Obriši
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5">Nema proizvoda u ovoj kategoriji.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
};

export default AdminPanel;
