import React, { useState, useEffect } from "react";

const AdminPanel = () => {
  const [proizvodi, setProizvodi] = useState([]);
  const [noviProizvod, setNoviProizvod] = useState({
    naziv: "",
    cijena: "",
    opis: "",
    slika: "",
    gramaza: ""
  });
  const [kategorija, setKategorija] = useState(""); // obavezno da se izabere kategorija
  const [poruka, setPoruka] = useState("");

  // Učitaj proizvode iz svih kategorija
  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then(res => res.json())
      .then(data => setProizvodi(data.svi || []))
      .catch(err => console.error("Greška pri učitavanju proizvoda", err));
  }, []);

  const handleChange = e => {
    setNoviProizvod({ ...noviProizvod, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setPoruka("");

    // Provjera da li je kategorija odabrana
    if (!kategorija) {
      setPoruka("⚠️ Molimo odaberite kategoriju.");
      return;
    }

    // Provjera da su obavezna polja popunjena
    if (
      !noviProizvod.naziv.trim() ||
      !noviProizvod.cijena.trim() ||
      !noviProizvod.opis.trim() ||
      !noviProizvod.slika.trim() ||
      !noviProizvod.gramaza.trim()
    ) {
      setPoruka("⚠️ Molimo popunite sva polja.");
      return;
    }

    try {
      // POST na endpoint za dodavanje proizvoda u kategoriju
      const response = await fetch("http://localhost:5000/api/products/category", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ proizvod: noviProizvod, kategorija }),
      });

      const data = await response.json();

      if (response.ok) {
        setPoruka("✅ Proizvod je uspješno dodan!");
        setProizvodi(prev => [...prev, noviProizvod]);
        setNoviProizvod({ naziv: "", cijena: "", opis: "", slika: "", gramaza: "" });
        setKategorija("");
      } else {
        setPoruka(`❌ Greška: ${data.error || "Nepoznata greška"}`);
      }
    } catch (err) {
      setPoruka("⚠️ Greška pri dodavanju proizvoda.");
      console.error(err);
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>🛠 Admin Panel</h2>

      <form onSubmit={handleSubmit} style={{ marginBottom: "2rem" }}>
        <input
          type="text"
          name="naziv"
          placeholder="Naziv"
          value={noviProizvod.naziv}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="gramaza"
          placeholder="Gramaža (npr. 250g)"
          value={noviProizvod.gramaza}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="cijena"
          placeholder="Cijena (npr. 12.50)"
          value={noviProizvod.cijena}
          onChange={handleChange}
          required
          step="0.01"
        />
        <input
          type="text"
          name="opis"
          placeholder="Opis"
          value={noviProizvod.opis}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="slika"
          placeholder="URL slike"
          value={noviProizvod.slika}
          onChange={handleChange}
          required
        />

        <select
          value={kategorija}
          onChange={e => setKategorija(e.target.value)}
          required
        >
          <option value="">-- Odaberite kategoriju --</option>
          <option value="preporuke">Preporuke</option>
          <option value="best">Best</option>
          <option value="svi">Svi</option>
        </select>

        <button type="submit">Dodaj artikal</button>
      </form>

      {poruka && <p>{poruka}</p>}

      <h3>📦 Postojeći proizvodi ({proizvodi.length})</h3>
      <ul>
        {proizvodi.map((p, i) => (
          <li key={i}>{p.naziv} – {p.cijena} $</li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPanel;
