import React, { useEffect, useState } from "react";
import CoffeeCard from "./CoffeeCard";

const AllProducts = ({ onAddToCart }) => {
  const [proizvodi, setProizvodi] = useState([]);

  useEffect(() => {
    const fetchProizvodi = async () => {
      try {
        const response = await fetch("/api/products");
        const data = await response.json();

        // Spajamo sve proizvode iz svih kategorija
        const sviProizvodi = Object.values(data).flat();
        setProizvodi(sviProizvodi);
      } catch (error) {
        console.error("Greška prilikom učitavanja proizvoda:", error);
      }
    };

    fetchProizvodi();
  }, []);

  return (
    <div className="ponuda-container">
      <h2>Svi proizvodi</h2>
      <div className="ponuda-grid">
        {proizvodi.map((proizvod, index) => (
          <CoffeeCard key={index} proizvod={proizvod} onAddToCart={onAddToCart} />
        ))}
      </div>
    </div>
  );
};

export default AllProducts;
