import React from "react";

const Korpa = ({ korpa, ukloniIzKorpe, promeniKolicinu }) => {
  return (
    <div>
      <h2>Korpa</h2>
      {korpa.length === 0 ? (
        <p>Korpa je prazna.</p>
      ) : (
        korpa.map((item, index) => (
          <div key={index} style={{ display: "flex", gap: "1rem", marginBottom: "1rem" }}>
            <span>{item.naziv}</span>
            <input
              type="number"
              min="1"
              value={item.kolicina}
              onChange={(e) => promeniKolicinu(item.naziv, parseInt(e.target.value))}
              style={{ width: "60px" }}
            />
            <button onClick={() => ukloniIzKorpe(item.naziv)}>Ukloni</button>
          </div>
        ))
      )}
    </div>
  );
};

export default Korpa;
