import React, { useEffect, useState } from 'react';

function AdminPanel() {
  const [podaci, setPodaci] = useState(null);
  const [noviProizvod, setNoviProizvod] = useState({
    naziv: '',
    opis: '',
    gramaza: '',
    cijena: '',
    slika: ''
  });

  // Učitavanje podataka iz data.json
  useEffect(() => {
    fetch('/data.json')
      .then(res => res.json())
      .then(data => setPodaci(data));
  }, []);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setNoviProizvod(prev => ({ ...prev, [name]: value }));
  };

  const dodajNoviProizvod = () => {
    const novi = [...podaci.svi, noviProizvod];
    const novoStanje = { ...podaci, svi: novi };
    setPodaci(novoStanje);
    alert('Dodano u "svi" (lokalno)');
  };

  const dodajUKategoriju = (index, kategorija) => {
    const proizvod = podaci.svi[index];
    const postoji = podaci[kategorija].some(p => p.naziv === proizvod.naziv);

    if (postoji) {
      alert('Već postoji u ' + kategorija);
      return;
    }

    const novaKategorija = [...podaci[kategorija], proizvod];
    const novoStanje = { ...podaci, [kategorija]: novaKategorija };
    setPodaci(novoStanje);
    alert(`Dodano u ${kategorija} (lokalno)`);
  };

  if (!podaci) return <div>Učitavanje podataka...</div>;

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Svi proizvodi</h2>
      <div style={{ display: 'grid', gap: '1rem' }}>
        {podaci.svi.map((proizvod, index) => (
          <div key={index} style={{ border: '1px solid gray', padding: '1rem' }}>
            <h3>{proizvod.naziv}</h3>
            <p>{proizvod.opis}</p>
            <p>{proizvod.gramaza}</p>
            <p>{proizvod.cijena}</p>
            <img src={proizvod.slika} alt={proizvod.naziv} width="100" />
            <div>
              <button onClick={() => dodajUKategoriju(index, 'preporuke')}>Dodaj u Preporuke</button>
              <button onClick={() => dodajUKategoriju(index, 'best')}>Dodaj u Best</button>
            </div>
          </div>
        ))}
      </div>

      <hr style={{ margin: '2rem 0' }} />

      <h2>Dodaj novi proizvod</h2>
      <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
        <input name="naziv" placeholder="Naziv" onChange={handleInputChange} />
        <input name="opis" placeholder="Opis" onChange={handleInputChange} />
        <input name="gramaza" placeholder="Gramaža" onChange={handleInputChange} />
        <input name="cijena" placeholder="Cijena" onChange={handleInputChange} />
        <input name="slika" placeholder="Putanja slike" onChange={handleInputChange} />
        <button onClick={dodajNoviProizvod}>Dodaj u svi</button>
      </div>
    </div>
  );
}

export default AdminPanel;
