import React from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Navbar.css"; // ako koristiš CSS fajl
import { useEffect, useState } from "react";

const Navbar = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    setIsLoggedIn(!!user);
    setIsAdmin(user?.email === "admin@admin.com");
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setIsLoggedIn(false);
    setIsAdmin(false);
    navigate("/");
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="nav-link">Početna</Link>
        <Link to="/onama" className="nav-link">O nama</Link>
        <Link to="/ponuda" className="nav-link">Ponuda</Link>
        <Link to="/kontakt" className="nav-link">Kontakt</Link>
      </div>
      <div className="navbar-right">
        {!isLoggedIn && (
          <>
            <Link to="/prijava" className="nav-link">Prijava</Link>
            <Link to="/registracija" className="nav-link">Registracija</Link>
          </>
        )}
        {isLoggedIn && (
          <>
            {isAdmin ? (
              <Link to="/admin" className="nav-link">Admin Panel</Link>
            ) : (
              <Link to="/korpa" className="nav-link">Korpa</Link>
            )}
            <button onClick={handleLogout} className="nav-button">Odjava</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
