import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../css";

const Navbar = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [menuActive, setMenuActive] = useState(false); // za hamburger menu

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    setIsLoggedIn(!!user);
    setIsAdmin(user?.email === "admin@admin.com");
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setIsLoggedIn(false);
    setIsAdmin(false);
    navigate("/");
  };

  const toggleMenu = () => {
    setMenuActive(!menuActive);
  };

  return (
    <nav className={`navbar ${menuActive ? "active" : ""}`}>
      <div className="left-group">
        <ul className="nav-links">
          <li><Link to="/" className="nav-link">Poƒçetna</Link></li>
          <li><Link to="/onama" className="nav-link">O nama</Link></li>
          <li><Link to="/ponuda" className="nav-link">Ponuda</Link></li>
          <li><Link to="/kontakt" className="nav-link">Kontakt</Link></li>
        </ul>
      </div>

      <div className="auth-buttons">
        {!isLoggedIn && (
          <>
            <Link to="/prijava" className="btn">Prijava</Link>
            <Link to="/registracija" className="btn primary">Registracija</Link>
          </>
        )}
        {isLoggedIn && (
          <>
            {isAdmin ? (
              <Link to="/admin" className="btn">Admin Panel</Link>
            ) : (
              <Link to="/korpa" className="btn">Korpa</Link>
            )}
            <button onClick={handleLogout} className="btn">Odjava</button>
          </>
        )}
      </div>

      <div className="hamburger" onClick={toggleMenu}>
        &#9776;
      </div>
    </nav>
  );
};

export default Navbar;
