import React, { useState, useEffect } from "react";
import "../styles/admin.css";

const AdminPanel = () => {
  const [naziv, setNaziv] = useState("");
  const [opis, setOpis] = useState("");
  const [gramaza, setGramaza] = useState("");
  const [cijena, setCijena] = useState("");
  const [slika, setSlika] = useState("");
  const [podaci, setPodaci] = useState({ preporuke: [], best: [], svi: [] });

  const ucitajPodatke = async () => {
    const res = await fetch("http://localhost:5000/api/products");
    const data = await res.json();
    setPodaci(data);
  };

  useEffect(() => {
    ucitajPodatke();
  }, []);

  const handleDodaj = async (e) => {
    e.preventDefault();
    const novi = { naziv, opis, gramaza, cijena, slika };
    const res = await fetch("http://localhost:5000/api/products/svi", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(novi),
    });
    if (res.ok) {
      ucitajPodatke();
      setNaziv("");
      setOpis("");
      setGramaza("");
      setCijena("");
      setSlika("");
    }
  };

  const dodajUKategoriju = async (naziv, kategorija) => {
    const proizvod = podaci.svi.find(p => p.naziv === naziv);
    const res = await fetch(`http://localhost:5000/api/dodaj/${kategorija}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(proizvod),
    });
    if (res.ok) ucitajPodatke();
  };

  const obrisiIzKategorije = async (kategorija, naziv) => {
    const res = await fetch(`http://localhost:5000/api/obrisi/${kategorija}/${naziv}`, {
      method: "DELETE",
    });
    if (res.ok) ucitajPodatke();
  };

  const renderTabela = (naslov, kategorija) => (
    <div>
      <h3>{naslov}</h3>
      <table className="admin-table">
        <thead>
          <tr>
            <th>Naziv</th>
            <th>Akcija</th>
          </tr>
        </thead>
        <tbody>
          {podaci[kategorija]?.map((p) => (
            <tr key={p.naziv}>
              <td>{p.naziv}</td>
              <td>
                <button className="btn-danger" onClick={() => obrisiIzKategorije(kategorija, p.naziv)}>
                  Obriši
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="admin-panel">
      <h2>Dodaj novi proizvod (automatski ide u "svi")</h2>
      <form onSubmit={handleDodaj}>
        <input type="text" placeholder="Naziv" value={naziv} onChange={(e) => setNaziv(e.target.value)} /><br />
        <textarea placeholder="Opis" value={opis} onChange={(e) => setOpis(e.target.value)} /><br />
        <input type="text" placeholder="Gramaža" value={gramaza} onChange={(e) => setGramaza(e.target.value)} /><br />
        <input type="text" placeholder="Cijena" value={cijena} onChange={(e) => setCijena(e.target.value)} /><br />
        <input type="text" placeholder="Slika" value={slika} onChange={(e) => setSlika(e.target.value)} /><br />
        <button type="submit">Dodaj</button>
      </form>

      {renderTabela("Preporuke (max 4)", "preporuke")}
      {renderTabela("Best (max 4)", "best")}

      <h3>Svi proizvodi</h3>
      <table className="admin-table">
        <thead>
          <tr>
            <th>Naziv</th>
            <th>Dodaj u preporuke</th>
            <th>Dodaj u best</th>
            <th>Obriši</th>
          </tr>
        </thead>
        <tbody>
          {podaci.svi.map((p) => (
            <tr key={p.naziv}>
              <td>{p.naziv}</td>
              <td>
                <button onClick={() => dodajUKategoriju(p.naziv, "preporuke")} disabled={podaci.preporuke.length >= 4}>
                  + Preporuke
                </button>
              </td>
              <td>
                <button onClick={() => dodajUKategoriju(p.naziv, "best")} disabled={podaci.best.length >= 4}>
                  + Best
                </button>
              </td>
              <td>
                <button className="btn-danger" onClick={() => obrisiIzKategorije("svi", p.naziv)}>
                  Obriši
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
