import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import "../css/style.css";

const Navbar = ({ isLoggedIn = false, setIsLoggedIn, isAdmin = false, setIsAdmin }) => {
  const [navActive, setNavActive] = useState(false);
  const location = useLocation();

  useEffect(() => {
    // Postavi admin status na osnovu rute
    if (setIsAdmin) {
      if (location.pathname.startsWith("/admin")) {
        setIsAdmin(true);
      } else {
        setIsAdmin(false);
      }
    }

    // Provjeri da li postoji token u localStorage
    const korisnik = JSON.parse(localStorage.getItem("korisnik"));
    if (setIsLoggedIn) {
      if (korisnik && korisnik.token) {
        setIsLoggedIn(true);
        if (korisnik.isAdmin) setIsAdmin(true);
      } else {
        setIsLoggedIn(false);
        setIsAdmin(false);
      }
    }
  }, [location, setIsAdmin, setIsLoggedIn]);

  const toggleNav = () => setNavActive(!navActive);
  const closeNav = () => setNavActive(false);

  function logout() {
    localStorage.removeItem("korisnik");
    if (setIsLoggedIn) setIsLoggedIn(false);
    if (setIsAdmin) setIsAdmin(false);
    closeNav();
  }

  return (
    <nav className={`navbar ${navActive ? "active" : ""}`}>
      <div className="hamburger" onClick={toggleNav}>
        &#9776;
      </div>

      <div className="left-group">
        <div className="logo">Atelier Noir</div>
        <ul className="nav-links">
          <li><Link to="/" onClick={closeNav}>Poƒçetna</Link></li>
          <li><Link to="/o_nama" onClick={closeNav}>O nama</Link></li>
          <li><Link to="/ponuda" onClick={closeNav}>Ponuda</Link></li>
          <li><Link to="/kontakt" onClick={closeNav}>Kontakt</Link></li>
        </ul>
      </div>

      <div className="auth-buttons">
        {!isLoggedIn ? (
          <Link to="/sign-up" onClick={closeNav} className="btn">Registracija</Link>
        ) : (
          <>
            {isAdmin ? (
              <Link to="/admin" onClick={closeNav} className="btn">Admin Panel</Link>
            ) : (
              <Link to="/korpa" onClick={closeNav} className="btn">Korpa</Link>
            )}
            <button onClick={logout} className="btn logout-btn">Odjava</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
